
public class If_loop_5 {

	public void number() {
		for (int i = 500; i <= 1000; i++) {
			if (i == 808) {
				System.out.println("Number found" + " " + i);
				break;

			}
		}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		If_loop_5 Ifloop = new If_loop_5();
		Ifloop.number();
	}

}
